package com.company;import java.util.Scanner;public class CharcterMultiplier_02 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String[] input = scanner.nextLine().split(" ");        String text1 = input[0];        String text2 = input[1];        int maxLenght = Math.max(text1.length(),text2.length());        int sum = 0;        for (int i = 0; i < maxLenght ; i++) {            if (maxLenght == text1.length() && maxLenght == text2.length()){                char symbolText1 = text1.charAt(i);                char symbolText2 = text2.charAt(i);                sum += symbolText1 * symbolText2;            }            else if (maxLenght == text1.length()){                char symbolText1 = text1.charAt(i);                if (i <= text2.length() - 1){                    char symbolText2 = text2.charAt(i);                    sum += symbolText1 * symbolText2;                }else {                    sum += symbolText1;                }            }else if  (maxLenght == text2.length()){                char symbolText2 = text2.charAt(i);                if (i <= text1.length() - 1){                    char symbolText1 = text1.charAt(i);                    sum += symbolText1 * symbolText2;                }else {                    sum += symbolText2;                }            }        }        System.out.println(sum);            }}