package com.company;import javax.security.sasl.SaslClient;import java.util.Scanner;public class ExtractFile_03 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String [] program = scanner.nextLine().split("\\\\");        String[] file = program[program.length - 1].split("\\.");        String fileName = file[0];        String fileExtension = file[1];        System.out.printf("File name: %s%nFile extension: %s",fileName,fileExtension);    }}