package com.company;import java.util.Arrays;import java.util.List;import java.util.Scanner;import java.util.stream.Collectors;public class Largest3Numbers {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        List<Integer> number = Arrays.stream(scanner.nextLine().split("\\s+"))                .map(Integer::parseInt).sorted((e1, e2) -> e2.compareTo(e1)).collect(Collectors.toList());        for (int i = 0; i < number.size(); i++) {            if (i == 3) {                break;            }            System.out.print(number.get(i) + " ");        }    }}