package com.company;import java.util.Arrays;import java.util.LinkedHashMap;import java.util.Map;import java.util.Scanner;public class CountCharsInaString_01 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String text = scanner.nextLine();        Map<Character, Integer> countChars = new LinkedHashMap<>();        for (int i = 0; i < text.length(); i++) {            char currentChar = text.charAt(i);            if (currentChar != ' ') {                if (!countChars.containsKey(currentChar)) {                    countChars.put(currentChar, 1);                } else {                    countChars.put(currentChar, countChars.get(currentChar) + 1);                }            }        }        countChars.forEach((key, value) -> System.out.println(key + " -> " + value));    }}