package com.company;import java.util.*;import java.util.stream.Collectors;public class Courses {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String input = scanner.nextLine();        Map<String, List<String>> studentList = new LinkedHashMap<>();        while (!input.equals("end")) {            String courses = input.split(" : ")[0];            String studentName = input.split(" : ")[1];            if (!studentList.containsKey(courses)) {                studentList.put(courses, new ArrayList<>());                studentList.get(courses).add(studentName);            } else {                studentList.get(courses).add(studentName);            }            input = scanner.nextLine();        }        studentList                .entrySet()                .stream()                .sorted((e1, e2) -> Integer.compare(e2.getValue().size(), e1.getValue().size()))                .forEach(e -> {                    System.out.println(e.getKey() + ": " + e.getValue().size());                    e.getValue().stream()                            .sorted(String::compareTo)                            .forEach(v -> System.out.println("-- " + v));                });        List<String> name = studentList.entrySet().stream().map(e -> e.getKey()).collect(Collectors.toList());    }}