package com.company;import java.util.*;public class Orders_04 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String input = scanner.nextLine();        Map<String, List<Double>> products = new LinkedHashMap<>();        while (!input.equals("buy")) {            String typeProduct = input.split(" ")[0];            double priceProduct = Double.parseDouble(input.split(" ")[1]);            double quantityProduct = Double.parseDouble(input.split(" ")[2]);            if (!products.containsKey(typeProduct)) {                products.put(typeProduct, new ArrayList<>());                products.get(typeProduct).add(0, priceProduct);                products.get(typeProduct).add(1, quantityProduct);            } else {                products.get(typeProduct).set(0, priceProduct);                double currentQuantity = products.get(typeProduct).get(1);                products.get(typeProduct).set(1, currentQuantity + quantityProduct);            }            input = scanner.nextLine();        }        products.forEach((k, v) -> System.out.printf("%s -> %.2f%n", k, v.get(0) * v.get(1)));    }}