package com.company;import java.util.LinkedHashMap;import java.util.Map;import java.util.Scanner;public class SoftUniParking_05 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int countinput = Integer.parseInt(scanner.nextLine());        Map<String, String> user = new LinkedHashMap<>();        for (int i = 0; i < countinput; i++) {            String input = scanner.nextLine();            String command = input.split(" ")[0];            String name = input.split(" ")[1];            if (command.equals("register")) {                String regNumber = input.split(" ")[2];                if (!user.containsKey(name)) {                    user.put(name, regNumber);                    System.out.printf("%s registered %s successfully%n", name, regNumber);                } else {                    System.out.printf("ERROR: already registered with plate number %s%n", user.get(name));                }            } else if (command.equals("unregister")) {                if (!user.containsKey(name)) {                    System.out.printf("ERROR: user %s not found%n", name);                } else {                    System.out.printf("%s unregistered successfully%n", name);                    user.remove(name);                }            }        }        user.forEach((k, v) -> System.out.printf("%s => %s%n", k, v));    }}