package com.company;import java.util.ArrayList;import java.util.List;import java.util.Scanner;public classReplaceRepeatingChars_06 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        char[] character = scanner.nextLine().toCharArray();        List<Character> characters = new ArrayList<>();        for (int i = 0; i <character.length ; i++) {            characters.add(character[i]);        }        for (int i = 0; i < characters.size() ; i++) {            if (characters.size() - 1 == i){                break;            }            if (characters.get(i) == characters.get(i + 1)){                characters.remove(i);                i = -1;            }        }        for (Character ch : characters) {            System.out.print(ch);        }    }}