package com.company;import java.util.ArrayList;import java.util.List;import java.util.Scanner;public class StringExplosion_07 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        char[] input = scanner.nextLine().toCharArray();        List<Character> stringExplotion = new ArrayList<>();        for (int i = 0; i < input.length; i++) {            stringExplotion.add(input[i]);        }        int power = 0;        for (int i = 0; i < stringExplotion.size(); i++) {            if (Character.isDigit(stringExplotion.get(i))) {                String currentNumber = stringExplotion.get(i).toString();                 power += power = Integer.parseInt(currentNumber);                int counter = 0;                for (int j = i; j < stringExplotion.size(); j++) {                    if (stringExplotion.get(i) == '>') {                        power = power - counter;                        break;                    }                    counter++;                    stringExplotion.remove(i);                    j = i - 1;                    if (counter == power) {                        power = 0;                        break;                    }                }            }        }        stringExplotion.forEach(e -> System.out.print(e.charValue()));    }}