package com.company;import java.util.Scanner;public class CeaserHipser_04 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String text = scanner.nextLine();        StringBuilder stringBuilder = new StringBuilder();        for (int i = 0; i < text.length(); i++) {            char currentSymbol = text.charAt(i);            int finalSymbol = currentSymbol + 3;            stringBuilder.append(Character.toChars(finalSymbol));        }        System.out.println(stringBuilder.toString());    }}