package com.company;import java.util.ArrayList;import java.util.List;import java.util.Scanner;import java.util.regex.Matcher;import java.util.regex.Pattern;public class MatchPhoneNumber_03 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String inputPhoneNumbers = scanner.nextLine();        String regex = "\\+359([ \\-])[2]\\1([0-9]{3})\\1([0-9]{4})\\b";        Pattern pattern = Pattern.compile(regex);        Matcher matcherCorrectPhoneNumber = pattern.matcher(inputPhoneNumbers);        List<String> correctPhoneNumber = new ArrayList<>();        while (matcherCorrectPhoneNumber.find()) {            correctPhoneNumber.add(matcherCorrectPhoneNumber.group());        }        for (int i = 0; i < correctPhoneNumber.size(); i++) {            if (i == correctPhoneNumber.size() - 1) {                System.out.print(correctPhoneNumber.get(i));                break;            }            System.out.print(correctPhoneNumber.get(i) + ", ");        }    }}