package com.company;import java.util.*;import java.util.regex.Matcher;import java.util.regex.Pattern;import java.util.stream.Collectors;public class Rice_02 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        List<String> listName = Arrays.stream(scanner.nextLine().split(", "))                .collect(Collectors.toList());        Map<String, Integer> ricer = new TreeMap<>();        for (int i = 0; i < listName.size(); i++) {            ricer.put(listName.get(i), 0);        }        String input = scanner.nextLine();        String regexDigit = "[0-9]";        Pattern pattern = Pattern.compile(regexDigit);        String regexLetter = "[A-Za-z]";        Pattern patternLetter = Pattern.compile(regexLetter);        while (!input.equals("end of race")) {            int distances = 0;            StringBuilder stringBuilder = new StringBuilder();            String name = "";            Matcher matcher = pattern.matcher(input);            while (matcher.find()) {                int digit = Integer.parseInt(matcher.group());                distances += digit;            }            Matcher matcherLetter = patternLetter.matcher(input);            while (matcherLetter.find()) {                String letter = matcherLetter.group();                stringBuilder.append(letter);            }            name = stringBuilder.toString();            if (ricer.containsKey(name)) {                ricer.put(name, ricer.get(name) + distances);            }            input = scanner.nextLine();        }        List<String> name = ricer.entrySet()                .stream()                .sorted((e1, e2) -> Integer.compare(e2.getValue(), e1.getValue()))                .limit(3)                .map(Map.Entry::getKey).collect(Collectors.toList());        System.out.printf("1st place: %s%n", name.get(0));        System.out.printf("2nd place: %s%n", name.get(1));        System.out.printf("3rd place: %s", name.get(2));    }}