package com.company;import java.util.Scanner;import java.util.regex.Matcher;import java.util.regex.Pattern;public class ExtractEmails_06 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String input = scanner.nextLine();        String regexEmail = "\\b(([a-zA-Z0-9]+)([\\.\\-\\_]?)([a-zA-Z0-9]+))@(([a-zA-Z]\\-?)+\\.[a-zA-z]+\\.?[a-zA-Z]*)\\b";        Pattern pattern = Pattern.compile(regexEmail);        Matcher matcher = pattern.matcher(input);        while (matcher.find()) {            System.out.println(matcher.group());        }    }}