package com.company;import java.util.Scanner;public class EqualArrays_06 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String[] input1 = scanner.nextLine().split(" ");        String[] input2 = scanner.nextLine().split(" ");        int[] numbers1 = new int[input1.length];        int[] numbers2 = new int[input2.length];        for (int i = 0; i < numbers1.length; i++) {            numbers1[i] = Integer.parseInt(input1[i]);        }        for (int i = 0; i < numbers2.length; i++) {            numbers2[i] = Integer.parseInt(input2[i]);        }        int sum = 0;        if (numbers1.length == numbers2.length) {            for (int i = 0; i < numbers1.length; i++) {                if (numbers1[i] == numbers2[i]) {                    sum += numbers1[i];                } else {                    System.out.printf("Arrays are not identical. Found difference at %d index.", i);                    return;                }            }            System.out.printf("Arrays are identical. Sum: %d", sum);        }    }}