package com.company;import java.util.Scanner;public class SumEvenNumbers_03 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String[] numberLine = scanner.nextLine().split(" ");        int[] numbers = new int[numberLine.length];        int sum = 0;        for (int i = 0; i < numbers.length; i++) {            numbers[i] = Integer.parseInt(numberLine[i]);            if (numbers[i] % 2 == 0) {                sum += numbers[i];            }        }        System.out.println(sum);    }}