package com.company;import java.util.Scanner;public class CommonElements_02 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String[] input1 = scanner.nextLine().split(" ");        String[] input2 = scanner.nextLine().split(" ");        for (String s : input2) {            for (int j = 0; j < input1.length; j++) {                if (s.equals(input1[j])) {                    System.out.print(s + " ");                }            }        }    }}