package com.company;import java.util.Scanner;public class EqualSums_06 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String[] input = scanner.nextLine().split(" ");        int[] numbers = new int[input.length];        for (int i = 0; i < numbers.length; i++) {            numbers[i] = Integer.parseInt(input[i]);        }        int rightSum = 0;        int leftSum = 0;                for (int i = 0; i < numbers.length; i++) {            for (int j = i + 1; j < numbers.length; j++) {                rightSum += numbers[j];            }            if (leftSum == rightSum) {                System.out.println(i);                break;            }            leftSum += numbers[i];            rightSum = 0;        }        if (leftSum != rightSum) {            System.out.println("no");        }    }}