package com.company;import java.util.Arrays;import java.util.List;import java.util.Scanner;import java.util.stream.Collectors;public class ListManipulationAdvanced_05 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        List<Integer> numbers = Arrays.stream(scanner.nextLine().split("\\s+"))                .map(Integer::parseInt).collect(Collectors.toList());        String command = scanner.nextLine();        while (!command.equals("end")) {            String[] currentCommand = command.split("\\s+");            switch (currentCommand[0]) {                case "Contains":                    if (numbers.contains(Integer.parseInt(currentCommand[1]))) {                        System.out.println("Yes");                    } else {                        System.out.println("No such number");                    }                    break;                case "Print":                    if (currentCommand[1].equals("even")) {                        printEvenNumbernLists(numbers);                    } else if (currentCommand[1].equals("odd")) {                        printOddNumbersInList(numbers);                    }                    break;                case "Get":                    printSumNumbersInList(numbers);                    break;                case "Filter":                    if (currentCommand[1].equals(">")) {                        printbiggerNumbers(numbers, Integer.parseInt(currentCommand[2]));                    } else if (currentCommand[1].equals("<")) {                        prinSmallNumber(numbers, Integer.parseInt(currentCommand[2]));                    } else if (currentCommand[1].equals(">=")) {                        int currentNumber = Integer.parseInt(currentCommand[2]);                        for (int i = 0; i < numbers.size(); i++) {                            if (numbers.get(i) >= currentNumber) {                                System.out.print(numbers.get(i) + " ");                            }                        }                        System.out.println();                    } else if (currentCommand[1].equals("<=")) {                        int currentNumbers = Integer.parseInt(currentCommand[2]);                        for (int i = 0; i < numbers.size(); i++) {                            if (numbers.get(i) <= currentNumbers) {                                System.out.print(numbers.get(i) + " ");                            }                        }                        System.out.println();                    }                    break;            }            command = scanner.nextLine();        }    }    private static void printEvenNumbernLists(List<Integer> numbers) {        for (int i = 0; i < numbers.size(); i++) {            if (numbers.get(i) % 2 == 0) {                System.out.print(numbers.get(i) + " ");            }        }        System.out.println();    }    private static void printOddNumbersInList(List<Integer> numbers) {        for (int i = 0; i < numbers.size(); i++) {            if (numbers.get(i) % 2 != 0) {                System.out.print(numbers.get(i) + " ");            }        }        System.out.println();    }    private static void printSumNumbersInList(List<Integer> numbers) {        int sum = 0;        for (int i = 0; i < numbers.size(); i++) {            sum += numbers.get(i);        }        System.out.println(sum);    }    private static void printbiggerNumbers(List<Integer> numbers, int number) {        for (int i = 0; i < numbers.size(); i++) {            if (numbers.get(i) > number) {                System.out.print(numbers.get(i) + " ");            }        }        System.out.println();    }    private static void prinSmallNumber(List<Integer> numbers, int number) {        for (int i = 0; i < numbers.size(); i++) {            if (numbers.get(i) < number) {                System.out.print(numbers.get(i) + " ");            }        }        System.out.println();    }}