package com.company;import java.util.ArrayList;import java.util.Arrays;import java.util.List;import java.util.Scanner;import java.util.stream.Collectors;public class MergingLists_03 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        List<Integer> input1 = Arrays.stream(scanner.nextLine().split(" "))                .map(Integer::parseInt).collect(Collectors.toList());        List<Integer> input2 = Arrays.stream(scanner.nextLine().split(" "))                .map(Integer::parseInt).collect(Collectors.toList());        int gerMaxLenghtLists = Math.max(input1.size(), input2.size());        List<Integer> generalLists = new ArrayList<>();        for (int i = 0; i < gerMaxLenghtLists; i++) {            if (i < input1.size()) {                generalLists.add(input1.get(i));            }            if (i < input2.size()) {                generalLists.add(input2.get(i));            }        }        for (int i = 0; i < generalLists.size(); i++) {            System.out.print(generalLists.get(i) + " ");        }    }}