package com.company;import java.util.Arrays;import java.util.List;import java.util.Scanner;import java.util.stream.Collectors;public class BombNumbers_05 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        List<Integer> numbers = Arrays.stream(scanner.nextLine().split("\\s+"))                .map(Integer::parseInt).collect(Collectors.toList());        int[] input = Arrays.stream(scanner.nextLine().split("\\s+"))                .mapToInt(Integer::parseInt).toArray();        int bombNumber = input[0];        int power = input[1];        if (power >= 0) {            while (numbers.contains(bombNumber)) {                int indexOfBombNumber = numbers.indexOf(bombNumber);                int currentPower = power;                for (int i = indexOfBombNumber + 1; i < numbers.size(); i++) {                    numbers.remove(i);                    currentPower = currentPower - 1;                    if (currentPower == 0) {                        break;                    }                    i = indexOfBombNumber;                }                currentPower = power + 1;                for (int i = indexOfBombNumber; i >= 0; i--) {                    numbers.remove(i);                    currentPower = currentPower - 1;                    if (currentPower == 0) {                        break;                    }                }            }            int sum = 0;            for (Integer number : numbers) {                sum += number;            }            System.out.println(sum);        }    }}