package com.company;import java.util.Arrays;import java.util.List;import java.util.Scanner;import java.util.stream.Collectors;public class CardsGame_06 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        List<Integer> card1 = Arrays.stream(scanner.nextLine().split("\\s+"))                .map(Integer::parseInt).collect(Collectors.toList());        List<Integer> card2 = Arrays.stream(scanner.nextLine().split("\\s+"))                .map(Integer::parseInt).collect(Collectors.toList());        int lenght = Math.max(card1.size() - 1, card2.size() - 1);        for (int i = 0; i <= lenght; i++) {            if (card1.size() <= 0 || card2.size() <= 0) {                break;            }            if (card1.get(i) > (card2.get(i))) {                int bigCard = card1.get(i);                int loseCard = card2.get(i);                card2.remove(i);                card1.remove(i);                card1.add(bigCard);                card1.add(loseCard);            } else if (card2.get(i) > card1.get(i)) {                int bigCard = card2.get(i);                int loseCard = card1.get(i);                card2.remove(i);                card1.remove(i);                card2.add(bigCard);                card2.add(loseCard);            } else {                card1.remove(i);                card2.remove(i);            }            i = -1;        }        int sum = 0;        if (card1.size() > 0) {            for (int i = 0; i < card1.size(); i++) {                sum += card1.get(i);            }            System.out.printf("First player wins! Sum: %d", sum);        } else {            for (int i = 0; i < card2.size(); i++) {                sum += card2.get(i);            }            System.out.printf("Second player wins! Sum: %d", sum);        }    }}