package com.company;import java.util.Arrays;import java.util.List;import java.util.Scanner;import java.util.stream.Collectors;public class ListOperations_04 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        List<Integer> numbers = Arrays.stream(scanner.nextLine().split("\\s+"))                .map(Integer::parseInt).collect(Collectors.toList());        String inputCommand = scanner.nextLine();        while (!inputCommand.equals("End")) {            String[] command = inputCommand.split("\\s+");            switch (command[0]) {                case "Add":                    numbers.add(Integer.parseInt(command[1]));                    break;                case "Insert":                    numbers.add(Integer.parseInt(command[2]), Integer.parseInt(command[1]));                    break;                case "Remove":                    if (Integer.parseInt(command[1]) <= numbers.size() - 1 && Integer.parseInt(command[1]) >= 0) {                        numbers.remove(Integer.parseInt(command[1]));                    } else {                        System.out.println("Invalid index");                    }                    break;                case "Shift":                    if (command[1].equals("left")) {                        for (int i = 1; i <= Integer.parseInt(command[2]); i++) {                            int current = numbers.get(0);                            numbers.remove(0);                            numbers.add(current);                        }                    } else if (command[1].equals("right")) {                        for (int i = 1; i <= Integer.parseInt(command[2]); i++) {                            int current = numbers.get(numbers.size() - 1);                            numbers.remove(numbers.size() - 1);                            numbers.add(0, current);                        }                    }                    break;            }            inputCommand = scanner.nextLine();        }        for (Integer number : numbers) {            System.out.print(number + " ");        }    }}