package com.company;import java.util.Arrays;import java.util.List;import java.util.Scanner;import java.util.stream.Collectors;public class Train_01 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        List<Integer> train = Arrays.stream(scanner.nextLine().split("\\s+"))                .map(Integer::parseInt).collect(Collectors.toList());        int capacityWagon = Integer.parseInt(scanner.nextLine());        String input = scanner.nextLine();        while (!input.equals("end")) {            String[] command = input.split("\\s+");            if (command[0].equals("Add")) {                train.add(Integer.parseInt(command[1]));            } else {                int people = Integer.parseInt(command[0]);                for (int i = 0; i < train.size(); i++) {                    if (train.get(i) + people <= capacityWagon) {                        train.set(i, train.get(i) + people);                        break;                    }                }            }            input = scanner.nextLine();        }        for (Integer wagon : train) {            System.out.print(wagon + " ");        }    }}