package com.company;import java.util.Random;import java.util.Scanner;public class RandomizeWords_01 {    public static void main(String[] args) {        Scanner scanner = new Scanner((System.in));        String[] input = scanner.nextLine().split("\\s+");        Random myRandom = new Random();        for (int i = 0; i < input.length; i++) {            int randomIndex = myRandom.nextInt(input.length);            String current = input[i];            input[i] = input[randomIndex];            input[randomIndex] = current;        }        for (String s : input) {            System.out.println(s);        }    }}