package com.company.song_04;import java.util.ArrayList;import java.util.List;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int countSongs = Integer.parseInt(scanner.nextLine());        List<Songs> myListSongs = new ArrayList<>();        for (int i = 1; i <= countSongs; i++) {            String[] song = scanner.nextLine().split("_");            String typList = song[0];            String name = song[1];            String time = song[2];            Songs songs = new Songs(typList, name, time);            myListSongs.add(songs);        }        String typeSong = scanner.nextLine();        for (Songs myListSong : myListSongs) {            if (myListSong.getTypeList().equals(typeSong)){                System.out.println(myListSong.getName());            }else if (typeSong.equals("all")){                System.out.println(myListSong.getName());            }        }    }}