package com.company.student_05;import java.util.ArrayList;import java.util.List;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String input = scanner.nextLine();        List<Students> listOfStudents = new ArrayList<>();        while (!input.equals("end")) {            String[] command = input.split("\\s+");            String firtsName = command[0];            String lastName = command[1];            int age = Integer.parseInt(command[2]);            String city = command[3];            Students students = new Students(firtsName, lastName, age, city);            listOfStudents.add(students);            input = scanner.nextLine();        }        String filterCity = scanner.nextLine();        for (Students listOfStudent : listOfStudents) {            if (listOfStudent.getHomeSity().equals(filterCity)){                System.out.printf("%s %s is %d years old%n",listOfStudent.getFirstName()                ,listOfStudent.getLastName(),listOfStudent.getAge());            }        }    }}