package com.company.student_05;public class Students {    private String firstName;    private String lastName;    private int age;    private String homeSity;    public Students(String firstName, String lastName, int age, String homeSity) {        this.firstName = firstName;        this.lastName = lastName;        this.age = age;        this.homeSity = homeSity;    }    public String getHomeSity() {        return homeSity;    }    public String getFirstName() {        return firstName;    }    public String getLastName() {        return lastName;    }    public int getAge() {        return age;    }}