package com.company.student_06;public class Students {    private final String firstName;    private final String lastName;    private final int age;    private final String homeTown;    public Students(String firstName, String lastName, int age, String homeTown) {        this.firstName = firstName;        this.lastName = lastName;        this.age = age;        this.homeTown = homeTown;    }    public String getFirstName() {        return firstName;    }    public String getLastName() {        return lastName;    }    public int getAge() {        return age;    }    public String getHomeTown() {        return homeTown;    }}