package com.company.articles_01;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String inputArticle = scanner.nextLine();        String currentTitle = inputArticle.split(", ")[0];        String currentContent = inputArticle.split(", ")[1];        String currentAuthor = inputArticle.split(", ")[2];        Articles articles = new Articles(currentTitle, currentContent, currentAuthor);        int countCommand = Integer.parseInt(scanner.nextLine());        for (int i = 0; i < countCommand; i++) {            String inputCommand = scanner.nextLine();            String command = inputCommand.split(": ")[0];            String newTitle = inputCommand.split(": ")[1];            switch (command) {                case "Edit" -> articles.edit(newTitle);                case "ChangeAuthor" -> articles.changeAuthor(newTitle);                case "Rename" -> articles.rename(newTitle);            }        }        System.out.println(articles);    }}