package com.company.articles_2;public class Articles_2 {    private String title;    private String content;    private String author;    public Articles_2(String title, String content, String author) {        this.title = title;        this.content = content;        this.author = author;    }    public String getTitle (){        return this.title;    }    public String getContent(){        return this.content;    }    public String getAuthor(){        return this.author;    }    @Override    public String toString() {        return title + " - " + content + ": " + author;    }}