package com.company.articles_2;import java.util.ArrayList;import java.util.Comparator;import java.util.List;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n = Integer.parseInt(scanner.nextLine());        List<Articles_2> articlesList = new ArrayList<>();        for (int i = 1; i <= n; i++) {            String input = scanner.nextLine();            String titleDate = input.split(", ")[0];            String contentDaate = input.split(", ")[1];            String authorDate = input.split(", ")[2];            Articles_2 articles_2 = new Articles_2(titleDate, contentDaate, authorDate);            articlesList.add(articles_2);        }        String sortCommand = scanner.nextLine();        switch (sortCommand) {            case "title" -> articlesList.sort(Comparator.comparing(Articles_2::getTitle));            case "content" -> articlesList.sort(Comparator.comparing(Articles_2::getContent));            case "author" -> articlesList.sort(Comparator.comparing(e -> e.getAuthor()));        }        for (Articles_2 articles_2 : articlesList) {            System.out.println(articles_2);        }    }}