package com.company.opinionPool_03;import java.util.ArrayList;import java.util.Comparator;import java.util.List;import java.util.Scanner;public class OpinionPool {    public static void main(String[] args) {            Scanner scanner = new Scanner(System.in);            int countInformation = Integer.parseInt(scanner.nextLine());            List<Person> personList = new ArrayList<>();            for (int i = 1; i <= countInformation; i++) {                String[] input = scanner.nextLine().split("\\s+");                String firstName = input[0];                int age = Integer.parseInt(input[1]);                if (age > 30) {                    Person person = new Person(firstName, age);                    personList.add(person);                }            }            personList.sort(Comparator.comparing(p -> p.getName()));            for (Person person : personList) {                System.out.println(person);            }        }    }