package com.company.orderByAge_06;import java.util.ArrayList;import java.util.Comparator;import java.util.List;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String input = scanner.nextLine();        List<People> person = new ArrayList<>();        while (!input.equals("End")){            String[] information = input.split("\\s+");            String name = information[0];            String id = information[1];            int age = Integer.parseInt(information[2]);            People people = new People(name,id,age);            person.add(people);            input = scanner.nextLine();        }        person.sort(Comparator.comparing(e -> e.getAge()));        for (People people : person) {            System.out.printf("%s with ID: %s is %d years old.%n",people.getName(),people.getId(),people.getAge());        }    }}