package com.company.student_04;import java.util.*;import java.util.stream.Collectors;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int countCommand = Integer.parseInt(scanner.nextLine());        List<Students> studentList = new ArrayList<>();        for (int i = 1; i <= countCommand ; i++) {            String[] input = scanner.nextLine().split("\\s+");            String firstName = input[0];            String lastName = input[1];            double grade = Double.parseDouble(input[2]);            Students students = new Students(firstName,lastName,grade);            studentList.add(students);        }        List<Students> student = studentList.stream()                .sorted(Comparator.comparing(Students::getGrade).reversed())                .collect(Collectors.toList());        for (Students students : student) {            System.out.println(students);        }    }}