package com.company.student_04;public class Students {    private String firstNme;    private String lastName;    private double grade;    public Students(String firstNme, String lastName, double grade) {        this.firstNme = firstNme;        this.lastName = lastName;        this.grade = grade;    }   public Double getGrade (){        return this.grade;   }    @Override    public String toString() {        return String.format("%s %s: %.2f",firstNme,lastName,grade);    }}