package com.company.vehicleCatalogue_05;import java.util.ArrayList;import java.util.List;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String input = scanner.nextLine();        List<CatalogueForVehicles> vehiclesList = new ArrayList<>();        int counterTruck = 0;        int counterCar = 0;        int hoursepowerCar = 0;        int hoursepowerTruck = 0;        while (!input.equals("End")) {            String[] vehicles = input.split(" ");            String type = vehicles[0];            String model = vehicles[1];            String color = vehicles[2];            int horsepower = Integer.parseInt(vehicles[3]);            CatalogueForVehicles catalogueForVehicles = new CatalogueForVehicles(type, model, color, horsepower);            vehiclesList.add(catalogueForVehicles);            if (type.equals("truck")) {                counterTruck++;                hoursepowerTruck += horsepower;            } else if (type.equals("car")) {                counterCar++;                hoursepowerCar += horsepower;            }            input = scanner.nextLine();        }        String modelVehicles = scanner.nextLine();        while (!modelVehicles.equals("Close the Catalogue")) {            for (int i = 0; i < vehiclesList.size(); i++) {                if (vehiclesList.get(i).getModel().equals(modelVehicles)) {                    String currentString = vehiclesList.get(i).getType();                    char currentChar = currentString.toUpperCase().charAt(0);                    String finalString = currentChar + currentString.substring(1);                    System.out.printf("Type: %s%nModel: %s%nColor: %s%nHorsepower: %d%n", finalString                            , vehiclesList.get(i).getModel(), vehiclesList.get(i).getColor()                            , vehiclesList.get(i).getHorsepower());                }            }            modelVehicles = scanner.nextLine();        }        double averageHorsepowerTruck = (hoursepowerTruck * 1.0) / counterTruck;        double averageHorsepowerCar = (hoursepowerCar * 1.0) / counterCar;        if (hoursepowerTruck == 0 ){            averageHorsepowerTruck = 0;        }if (hoursepowerCar == 0){            averageHorsepowerCar = 0;        }        System.out.printf("Cars have average horsepower of: %.2f.%n",averageHorsepowerCar);        System.out.printf("Trucks have average horsepower of: %.2f.",averageHorsepowerTruck);    }}