package com.company;import java.util.Scanner;public class GreaterofTwoValues_09 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String type = scanner.nextLine();        String a = scanner.next();        String b = scanner.next();        if (type.equals("int")) {            getMax(Integer.parseInt(a), Integer.parseInt(b));        } else if (type.equals("char")) {            getMax(a.charAt(0), b.charAt(0));        } else if (type.equals("string")) {            getMax(a, b);        }    }    public static int getMax(int a, int b) {        if (a > b) {            System.out.println(a);            return a;        } else {            System.out.println(b);            return b;        }    }    public static char getMax(char a, char b) {        if (a >= b) {            System.out.println(a);            return a;        } else {            System.out.println(b);            return b;        }    }    public static String getMax(String firstString, String secondString) {        if (firstString.length() > secondString.length()) {            System.out.println(firstString);            return firstString;        } else {            System.out.println(secondString);            return secondString;        }    }}