package com.company;import java.math.BigDecimal;import java.text.DecimalFormat;import java.util.Scanner;public class MathPower_08 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        double number = Double.parseDouble(scanner.nextLine());        int pow = Integer.parseInt(scanner.nextLine());        powerNumber(number, pow);    }    public static double powerNumber(double number, int pow) {        double powNumber = Math.pow(number, pow);            System.out.println( new DecimalFormat("0.####").format(powNumber));        return powNumber;    }}