package com.company;import java.util.Scanner;public class Mathoperations_11 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        double number1 = Double.parseDouble(scanner.nextLine());        char operation = scanner.nextLine().charAt(0);        double number2 = Double.parseDouble(scanner.nextLine());        calculation(number1, operation, number2);    }    public static void calculation(double number, char operation, double number2) {        double result = 0;        if (operation == '*') {            result = number * number2;            System.out.printf("%.0f", result);        } else if (operation == '+') {            result = number + number2;            System.out.printf("%.0f", result);        } else if (operation == '-') {            result = number - number2;            System.out.printf("%.0f", result);        } else if (operation == '/') {            result = number / number2;            System.out.printf("%.0f", result);        }    }}