package com.company;import java.util.Scanner;public class MultiplyEvensbyOdds_10 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int number = Math.abs(Integer.parseInt(scanner.nextLine()));        System.out.println(multyplySum(sumEven(number), sumOdd(number)));    }    public static int sumEven(int number) {        int sumEven = 0;        while (number > 0) {            int current = number % 10;            if (current % 2 == 0) {                sumEven += current;            }            number = number / 10;        }        return sumEven;    }    public static int sumOdd(int number) {        int sumOdd = 0;        while (number > 0) {            int current = number % 10;            if (current % 2 == 1) {                sumOdd += current;            }            number = number / 10;        }        return sumOdd;    }    public static int multyplySum(int evenNumber, int oddNumber) {        return evenNumber * oddNumber;    }}