package com.company;import java.util.Scanner;public class Orders_05 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String product = scanner.nextLine();        int quantity = Integer.parseInt(scanner.nextLine());        switch (product) {            case "coffee" -> totalPriceCoffee(quantity);            case "water" -> totalPriceWater(quantity);            case "coke" -> totalPriceCoke(quantity);            case "snacks" -> totalPriceSnacks(quantity);        }    }    public static void totalPriceCoffee(int quantity) {        double price = 1.50 * quantity;        System.out.printf("%.2f", price);    }    public static void totalPriceWater(int quantity) {        double price = 1.0 * quantity;        System.out.printf("%.2f", price);    }    public static void totalPriceCoke(int quantity) {        double totalPrice = 1.40 * quantity;        System.out.printf("%.2f", totalPrice);    }    public static void totalPriceSnacks(int qunatity) {        double totalPrice = 2.0 * qunatity;        System.out.printf("%.2f", totalPrice);    }}