package com.company;import java.util.Scanner;public class PrintingTriangle_03 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int heightTriange = Integer.parseInt(scanner.nextLine());        printTopOfTriangle(heightTriange);        printBottomTriangle(heightTriange);    }    public static void printTopOfTriangle(int heightTriange) {        for (int i = 1; i <= heightTriange; i++) {            printSingleLine(i);        }    }    public static void printSingleLine(int lenght) {        for (int i = 1; i <= lenght; i++) {            System.out.print(i + " ");        }        System.out.println();    }    public static void printBottomTriangle(int heightTriange) {        for (int i = heightTriange - 1; i >= 1; i--) {            printSingleLine(i);        }    }}