package com.company;import java.util.Scanner;public class RepeatString_07 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String input = scanner.nextLine();        int repeatCommand = Integer.parseInt(scanner.nextLine());        repeatText(input, repeatCommand);    }    public static void repeatText(String input, int repeat) {        StringBuilder text = new StringBuilder();        text.append(String.valueOf(input).repeat(Math.max(0, repeat)));        System.out.println(text.toString());    }}