package com.company;import java.util.Scanner;public class SignOfIntegerNumbers_01 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int number = Integer.parseInt(scanner.nextLine());        findPositiveNumber(number);        findNegativeNumber(number);        findZeroNumber(number);    }    public static void findPositiveNumber(int number) {        if (number > 0) {            System.out.printf("The number %d is positive.", number);        }    }    public static void findNegativeNumber(int number) {        if (number < 0) {            System.out.printf("The number %d is negative.", number);        }    }    public static void findZeroNumber(int number) {        if (number == 0) {            System.out.printf("The number %d is zero.", number);        }    }}