package com.company;import java.util.Scanner;public class AddAndSubtract_05 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int number1 = Integer.parseInt(scanner.nextLine());        int number2 = Integer.parseInt(scanner.nextLine());        int number3 = Integer.parseInt(scanner.nextLine());        System.out.println(substractMethods(sum(number1, number2), number3));    }    public static int sum(int a, int b) {        int sumOfNumbers = a + b;        return sumOfNumbers;    }    public static int substractMethods(int a, int b) {        int sum = a - b;        return sum;    }}