package com.company;import java.util.Scanner;public class FactorialDivision_08 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int number1 = Integer.parseInt(scanner.nextLine());        int number2 = Integer.parseInt(scanner.nextLine());        System.out.printf("%.2f", factorialDivision(findFactorial(number1), findFactorial(number2)));    }    public static long findFactorial(int number) {        long current = 1L;        for (int i = 1; i <= number; i++) {            current *= i;        }        return current;    }    public static double factorialDivision(double number1, double number2) {        return number1 / number2;    }}