package com.company;import java.util.Scanner;public class MiddleCharacters_06 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String input = scanner.next();        middleCharacters(input);    }    public static void middleCharacters(String input) {        if (input.length() % 2 == 1) {            System.out.println(input.charAt(input.length() / 2));        } else {            System.out.print(input.charAt(input.length() / 2 - 1));            System.out.print(input.charAt(input.length() / 2));        }    }}