package com.company;import java.util.Scanner;public class PasswordValidator_04 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String password = scanner.nextLine();        if (!validateLength(password)) {            System.out.println("Password must be between 6 and 10 characters");        }        if (!onlyLettersAndDigits(password)) {            System.out.println("Password must consist only of letters and digits");        }        if (!least2Digits(password)) {            System.out.println("Password must have at least 2 digits");        }        if (validateLength(password) && onlyLettersAndDigits(password) && least2Digits(password)) {            System.out.println("Password is valid");        }    }    static boolean validateLength(String pass) {        return pass.length() >= 6 && pass.length() <= 10;    }    static boolean onlyLettersAndDigits(String pass) {        for (int i = 0; i < pass.length(); i++) {            char symbol = pass.charAt(i);            boolean isSmallLetter = symbol >= 97 && symbol <= 122;            boolean isCapitalLetter = symbol >= 65 && symbol <= 90;            boolean isDigit = symbol >= 48 && symbol <= 57;            if (!isCapitalLetter && !isSmallLetter && !isDigit) {                return false;            }        }        return true;    }    static boolean least2Digits(String pass) {        int counter = 0;        for (char i = 0; i < pass.length(); i++) {            char symbol = pass.charAt(i);            boolean is2orMoreDigits = (symbol >= 48 && symbol <= 57);            if (is2orMoreDigits) {                counter++;            }        }        return counter >= 2;    }}