package com.company;import java.util.Scanner;public class SmallestOfThreeNumbers_01 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int a = Integer.parseInt(scanner.nextLine());        int b = Integer.parseInt(scanner.nextLine());        int c = Integer.parseInt(scanner.nextLine());        smallestOf3Numbers(a, b, c);    }    public static void smallestOf3Numbers(int a, int b, int c) {        int smalNumber = 0;        if (a < b && a < c) {            smalNumber = a;        } else if (b < a && b < c) {            smalNumber = b;        } else {            smalNumber = c;        }        System.out.println(smalNumber);    }}