package com.company;import java.util.Scanner;public class TopNumber_10 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int n = Integer.parseInt(scanner.nextLine());        for (int i = 1; i < n; i++) {            if (findOddNumber(i)) {                int sum = 0;                int temporary = i;                while (temporary > 0) {                    int currentNumber = temporary % 10;                    sum += currentNumber;                    temporary = temporary / 10;                }                if (sum % 8 == 0) {                    System.out.println(i);                }            }        }    }    public static boolean findOddNumber(int number) {        boolean oddNumber = false;        int counterOddNumber = 0;        while (number > 0) {            int currentNumber = number % 10;            if (currentNumber % 2 != 0) {                counterOddNumber++;                break;            }            number = number / 10;        }        if (counterOddNumber >= 1) {            oddNumber = true;        } else {            oddNumber = false;        }        return oddNumber;    }}